/**
 * Middleware para manejo centralizado de errores
 */

import { Request, Response, NextFunction } from 'express';
import logger from '../utils/logger';

// Interfaz para errores personalizados con c√≥digo de estado HTTP
interface ApiError extends Error {
  statusCode?: number;
  details?: unknown;
}

/**
 * Middleware para manejar errores de forma centralizada
 */
export const errorHandler = (
  err: ApiError,
  req: Request,
  res: Response,
  next: NextFunction
): void => {
  // Determinar el c√≥digo de estado (por defecto 500)
  const statusCode = err.statusCode || 500;

  // Registrar el error
  logger.error(`${statusCode} - ${err.message}`, {
    path: req.path,
    method: req.method,
    body: req.body,
    query: req.query,
    details: err.details || err.stack
  });

  // Determinar qu√© informaci√≥n enviar al cliente basado en el entorno
  const isProduction = process.env.NODE_ENV === 'production';

  // En producci√≥n no enviamos detalles t√©cnicos
  const responseBody = {
    success: false,
    error: {
      message: err.message,
      ...(isProduction ? {} : {
        stack: err.stack,
        details: err.details
      })
    }
  };

  // Enviar respuesta
  res.status(statusCode).json(responseBody);
};

/**
 * Helper para crear errores de API con c√≥digo de estado
 */
export const createApiError = (message: string, statusCode: number, details?: unknown): ApiError => {
  const error: ApiError = new Error(message);
  error.statusCode = statusCode;
  error.details = details;
  return error;
};

// Error 404 - Recurso no encontrado
export const notFoundHandler = (req: Request, res: Response, next: NextFunction): void => {
  const err: ApiError = new Error(`Ruta no encontrada: ${req.originalUrl}`);
  err.statusCode = 404;
  next(err);
};

/**
 * Middleware para registrar las solicitudes HTTP
 */

import { Request, Response, NextFunction } from 'express';
import logger from '../utils/logger';

/**
 * Middleware que registra informaci√≥n sobre cada solicitud HTTP
 */
export const requestLogger = (req: Request, res: Response, next: NextFunction): void => {
  // Guardar el tiempo de inicio
  const start = Date.now();

  // Capturar los datos originales de end para poder calcular el tiempo de respuesta
  const originalEnd = res.end;

  // Sobrescribir la funci√≥n end para registrar despu√©s de que la respuesta se haya enviado
  res.end = function(this: Response, chunk?: any, encoding?: any, callback?: any) {
    // Calcular el tiempo transcurrido
    const responseTime = Date.now() - start;

    // Registrar la solicitud
    logger.request(
      req.method,
      req.originalUrl || req.url,
      res.statusCode,
      responseTime
    );

    // Llamar a la funci√≥n original
    return originalEnd.call(this, chunk, encoding, callback);
  } as typeof res.end;

  next();
};

import { useState  } from 'react';
import { Box, Button, List, ListItem, ListItemIcon, ListItemText, Checkbox, Typography, Alert, CircularProgress, Stack, Chip } from '@mui/material';
import { trackEvent } from '@/core/lib/langfuse.client';
import axios from 'axios';
import { mcpApiClient } from '@/core/lib/csrf';

// Mapeo de campos internos a campos cl√≠nicos est√°ndar
const FIELD_MAPPING: Record<string, string> = {
  motivoConsulta: "anamnesis",
  hallazgosExploracion: "exploracion",
  diagnosticoFisioterapeutico: "diagnostico",
  tratamientoPropuesto: "plan"
};

interface ValidationAlert {
  type: string;
  message: string;
  field?: string;
}

interface AudioChecklistProps {
  patientId: string;
  visitId: string;
  onDataValidated: (data: { field: string; value: string }[]) => void;
}

export default function AudioChecklist(props: AudioChecklistProps) {
  // Desestructurar props correctamente
  const { patientId, visitId, onDataValidated } = props;

  const [isListening, setIsListening] = useState(false);
  const [isSaving, setIsSaving] = useState(false);
  const [isValidating, setIsValidating] = useState(false);
  const [validatedData, setValidatedData] = useState<Array<{
    field: string;
    value: string;
    isValid: boolean;
  }>>([]);
  const [validationResult, setValidationResult] = useState<{
    passed: boolean;
    alerts: ValidationAlert[];
    fieldsValidated: string[];
  } | null>(null);
  const [error, setError] = useState<string | null>(null);

  const startListening = () => {
    setIsListening(true);
    trackEvent("audio.start_listening", {
      timestamp: new Date().toISOString()
    });

    // Simular datos capturados por voz con los nuevos campos cl√≠nicos est√°ndar
    setTimeout(() => {
      const mockData = [
        { field: "motivoConsulta", value: "Paciente de 45 a√±os refiere dolor lumbar de caracter√≠sticas mec√°nicas desde hace 3 semanas. Sin antecedentes traum√°ticos. Refiere empeoramiento con actividades de carga o sedestaci√≥n prolongada y alivio parcial con reposo.", isValid: false },
        { field: "hallazgosExploracion", value: "Postura anti√°lgica. Limitaci√≥n a la flexi√≥n lumbar. Dolor a la palpaci√≥n en musculatura paravertebral. Prueba de Las√®gue negativa. No d√©ficit neurol√≥gico.", isValid: false },
        { field: "diagnosticoFisioterapeutico", value: "Lumbalgia mec√°nica inespec√≠fica", isValid: false },
        { field: "tratamientoPropuesto", value: "1. Terapia manual con t√©cnicas miofasciales. 2. Electroterapia analg√©sica. 3. Ejercicios de estabilizaci√≥n core. 4. Recomendaciones ergon√≥micas.", isValid: false }
      ];
      setValidatedData(mockData);
      setIsListening(false);
    }, 2000);
  };

  const toggleFieldValidation = (index: number) => {
    const newData = [...validatedData];
    newData[index].isValid = !newData[index].isValid;
    setValidatedData(newData);

    trackEvent("audio.validate_field", {
      field: newData[index].field,
      value: newData[index].value,
      validated: newData[index].isValid,
      timestamp: new Date().toISOString()
    });
  };

  const handleApproveData = async () => {
    const approvedFields = validatedData
      .filter(item => item.isValid)
      .map(item => ({ 
        field: item.field, 
        value: item.value 
      }));

    if (approvedFields.length === 0) {
      setError("Debes validar al menos un campo para continuar");
      return;
    }

    trackEvent("audio.approve_data", {
      fields: approvedFields,
      timestamp: new Date().toISOString()
    });

    // Primero notificamos al componente padre para actualizar el formulario
    onDataValidated(approvedFields);

    // Luego procesamos el flujo autom√°tico de validaci√≥n
    await processValidationFlow(approvedFields);
  };

  const processValidationFlow = async (approvedFields: { field: string; value: string }[]) => {
    setIsSaving(true);
    setError(null);

    try {
      // 1. Almacenar cada campo validado en /api/mcp/store
      const storePromises = approvedFields.map(async (item) => {
        // Mapear el campo interno al campo cl√≠nico est√°ndar
        const mappedField = FIELD_MAPPING[item.field];

        if (!mappedField) {
          console.warn(`Campo no mapeado: ${item.field}`);
          return null;
        }

        const storePayload = {
          visit_id: visitId,
          field: mappedField,
          role: "health_professional",
          content: item.value,
          overwrite: true
        };

        // Usar cliente protegido con CSRF
        const response = await mcpApiClient.post('/api/mcp/store', storePayload);
        return response.data;
      });

      // Esperar a que se guarden todos los campos
      const storeResults = await Promise.all(storePromises);

      // Verificar si alguno fall√≥
      const failedStores = storeResults.filter(result => !result || !result.success);
      if (failedStores.length > 0) {
        throw new Error(`No se pudieron almacenar ${failedStores.length} campos`);
      }

      // 2. Si todos se guardaron correctamente, ejecutar la validaci√≥n
      setIsSaving(false);
      setIsValidating(true);

      // Usar cliente protegido con CSRF
      const validateResponse = await mcpApiClient.get('/api/mcp/validate', {
        params: { visit_id: visitId }
      });

      setValidationResult({
        passed: validateResponse.data.validation_passed,
        alerts: validateResponse.data.alerts || [],
        fieldsValidated: validateResponse.data.fields_validated || []
      });

      trackEvent("validation.completed", {
        visit_id: visitId,
        passed: validateResponse.data.validation_passed,
        alerts_count: (validateResponse.data.alerts || []).length,
        timestamp: new Date().toISOString()
      });

    } catch (err) {
      console.error("Error en el flujo de validaci√≥n:", err);
      setError(err instanceof Error ? err.message : "Error inesperado en el proceso de validaci√≥n");

      trackEvent("validation.error", {
        visit_id: visitId,
        error: err instanceof Error ? err.message : "Error desconocido",
        timestamp: new Date().toISOString()
      });
    } finally {
      setIsSaving(false);
      setIsValidating(false);
    }
  };

  const getValidationSummary = () => {
    if (!validationResult) return null;

    const totalFields = 4; // anamnesis, exploracion, diagnostico, plan
    const registeredFields = validationResult.fieldsValidated.length;
    const alertCount = validationResult.alerts.length;

    let status = "‚úÖ Completo";
    if (alertCount > 0) {
      status = "‚ö†Ô∏è Revisi√≥n necesaria";
    } else if (registeredFields < totalFields) {
      status = "‚ö†Ô∏è Incompleto";
    }

    return { registeredFields, totalFields, alertCount, status };
  };

  return (
    <Box>
      <Box sx={{ mb: 2, display: 'flex', alignItems: 'center', gap: 2 }}>
        <Button
          variant="contained"
          color={isListening ? "error" : "primary"}
          onClick={startListening}
          disabled={isListening || isSaving || isValidating}
        >
          {isListening ? "üéôÔ∏è Escuchando..." : "üéôÔ∏è Iniciar Escucha"}
        </Button>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      {validatedData.length > 0 && (
        <>
          <Typography variant="subtitle1" sx={{ mb: 1 }}>
            Validar datos capturados:
          </Typography>
          <List>
            {validatedData.map((item, index) => (
              <ListItem key={item.field} dense onClick={() => toggleFieldValidation(index)}>
                <ListItemIcon>
                  <Checkbox
                    edge="start"
                    checked={item.isValid}
                    tabIndex={-1}
                    disableRipple
                  />
                </ListItemIcon>
                <ListItemText 
                  primary={item.field}
                  secondary={item.value}
                />
              </ListItem>
            ))}
          </List>

          <Button
            variant="contained"
            color="success"
            onClick={handleApproveData}
            disabled={!validatedData.some(item => item.isValid) || isSaving || isValidating}
            sx={{ mt: 2 }}
          >
            {isSaving ? (
              <>
                <CircularProgress size={20} sx={{ mr: 1, color: 'white' }} />
                Guardando...
              </>
            ) : isValidating ? (
              <>
                <CircularProgress size={20} sx={{ mr: 1, color: 'white' }} />
                Validando...
              </>
            ) : (
              '‚úÖ Aprobar y Validar Registros'
            )}
          </Button>
        </>
      )}

      {validationResult && (
        <Box sx={{ mt: 4, p: 2, border: '1px solid #e0e0e0', borderRadius: 1, bgcolor: '#f9f9f9' }}>
          <Typography variant="h6" gutterBottom>
            üîí Calidad cl√≠nica validada:
          </Typography>

          {getValidationSummary() && (
            <Stack spacing={1}>
              <Typography variant="body2">
                - Campos registrados: {getValidationSummary()?.registeredFields}/{getValidationSummary()?.totalFields}
              </Typography>
              <Typography variant="body2">
                - Alertas legales: {getValidationSummary()?.alertCount}
              </Typography>
              <Typography variant="body2">
                - Estado general: {getValidationSummary()?.status}
              </Typography>

              {validationResult.alerts.length > 0 && (
                <Box sx={{ mt: 2 }}>
                  <Typography variant="subtitle2">Detalles de las alertas:</Typography>
                  <Stack spacing={1} sx={{ mt: 1 }}>
                    {validationResult.alerts.map((alert, index) => (
                      <Alert key={index} severity="warning" sx={{ py: 0 }}>
                        <Stack direction="row" alignItems="center" spacing={1}>
                          <Chip size="small" label={alert.field || 'general'} />
                          {alert.message}
                        </Stack>
                      </Alert>
                    ))}
                  </Stack>
                </Box>
              )}
            </Stack>
          )}
        </Box>
      )}
    </Box>
  );
}

import React from 'react';
import { Paper, Typography, Divider, Grid, Chip } from '@mui/material';
import { MCPContext } from '../../core/mcp/CopilotContextBuilder';

interface PatientInfoCardProps {
  patientState: MCPContext['patient_state'];
  enrichment?: MCPContext['enrichment'];
}

// Definici√≥n de una interfaz para los datos del EMR del paciente para evitar errores
interface PatientEMRData {
  name?: string;
  allergies?: string[];
  chronicConditions?: string[];
  medications?: string[];
}

export const PatientInfoCard: React.FC<PatientInfoCardProps> = ({ patientState, enrichment }) => {
  // Creamos un objeto emulado para datos de EMR (estos vendr√≠an de otra fuente en la app real)
  const emrData: PatientEMRData = {
    name: "Paciente de Ejemplo", // Valor por defecto
    allergies: [],
    chronicConditions: [],
    medications: []
  };

  return (
    <Paper elevation={2} sx={{ p: 2 }}>
      <Typography variant="h6" gutterBottom>
        Informaci√≥n del Paciente
      </Typography>
      <Divider sx={{ mb: 2 }} />

      <Grid container spacing={2}>
        {/* Nombre del paciente */}
        {emrData?.name && (
          <Grid item xs={12}>
            <Typography variant="subtitle1" color="primary">
              {emrData.name}
            </Typography>
          </Grid>
        )}

        {/* Datos b√°sicos */}
        <Grid item xs={6}>
          <Typography variant="body2" color="text.secondary">
            Edad
          </Typography>
          <Typography variant="body1">
            {patientState.age} a√±os
          </Typography>
        </Grid>

        <Grid item xs={6}>
          <Typography variant="body2" color="text.secondary">
            Sexo
          </Typography>
          <Typography variant="body1">
            {patientState.sex === 'M' ? 'Masculino' :
             patientState.sex === 'F' ? 'Femenino' :
             'Otro'}
          </Typography>
        </Grid>

        {/* Alergias */}
        {emrData?.allergies && emrData.allergies.length > 0 && (
          <Grid item xs={12}>
            <Typography variant="body2" color="text.secondary" gutterBottom>
              Alergias
            </Typography>
            <div style={{ display: 'flex', gap: '0.5rem', flexWrap: 'wrap' }}>
              {emrData.allergies.map((allergy, index) => (
                <Chip
                  key={index}
                  label={allergy}
                  color="error"
                  size="small"
                  variant="outlined"
                />
              ))}
            </div>
          </Grid>
        )}

        {/* Condiciones Cr√≥nicas */}
        {emrData?.chronicConditions && emrData.chronicConditions.length > 0 && (
          <Grid item xs={12}>
            <Typography variant="body2" color="text.secondary" gutterBottom>
              Condiciones Cr√≥nicas
            </Typography>
            <div style={{ display: 'flex', gap: '0.5rem', flexWrap: 'wrap' }}>
              {emrData.chronicConditions.map((condition, index) => (
                <Chip
                  key={index}
                  label={condition}
                  color="primary"
                  size="small"
                  variant="outlined"
                />
              ))}
            </div>
          </Grid>
        )}

        {/* Medicamentos Actuales */}
        {emrData?.medications && emrData.medications.length > 0 && (
          <Grid item xs={12}>
            <Typography variant="body2" color="text.secondary" gutterBottom>
              Medicamentos Actuales
            </Typography>
            <div style={{ display: 'flex', gap: '0.5rem', flexWrap: 'wrap' }}>
              {emrData.medications.map((medication, index) => (
                <Chip
                  key={index}
                  label={medication}
                  color="info"
                  size="small"
                  variant="outlined"
                />
              ))}
            </div>
          </Grid>
        )}

        {/* Historia Cl√≠nica */}
        {patientState.history.length > 0 && (
          <Grid item xs={12}>
            <Typography variant="body2" color="text.secondary" gutterBottom>
              Historia Cl√≠nica Relevante
            </Typography>
            <div style={{ display: 'flex', gap: '0.5rem', flexWrap: 'wrap' }}>
              {patientState.history.map((item, index) => (
                <Chip
                  key={index}
                  label={item}
                  color="default"
                  size="small"
                  variant="outlined"
                />
              ))}
            </div>
          </Grid>
        )}
      </Grid>
    </Paper>
  );
};

import React from 'react';
import { Paper, Typography, Divider, List, ListItem, ListItemText } from '@mui/material';
import { format } from 'date-fns';
import { es } from 'date-fns/locale';
import { MCPContext } from '../../core/mcp/CopilotContextBuilder';

interface VisitHistoryCardProps {
  enrichment?: MCPContext['enrichment'];
}

// Interfaz para representar una visita m√©dica
interface Visit {
  id: string;
  date: string;
  type: string;
  summary: string;
  professional: string;
}

export const VisitHistoryCard: React.FC<VisitHistoryCardProps> = ({ enrichment }) => {
  // Datos de ejemplo de visitas (en una app real vendr√≠an de otra fuente)
  const visits: Visit[] = [];
  const recentVisits = visits.slice(0, 3); // Mostrar solo las √∫ltimas 3 visitas

  return (
    <Paper elevation={2} sx={{ p: 2 }}>
      <Typography variant="h6" gutterBottom>
        Historial de Visitas
      </Typography>
      <Divider sx={{ mb: 2 }} />

      {recentVisits.length === 0 ? (
        <Typography variant="body2" color="text.secondary">
          No hay visitas previas registradas
        </Typography>
      ) : (
        <List>
          {recentVisits.map((visit, index) => (
            <ListItem key={visit.id} divider={index < recentVisits.length - 1}>
              <ListItemText
                primary={
                  <Typography variant="subtitle2">
                    {format(new Date(visit.date), "d 'de' MMMM 'de' yyyy", { locale: es })}
                  </Typography>
                }
                secondary={
                  <>
                    <Typography variant="body2" color="text.secondary">
                      Tipo: {visit.type}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {visit.summary}
                    </Typography>
                    <Typography variant="caption" color="text.secondary">
                      Profesional: {visit.professional}
                    </Typography>
                  </>
                }
              />
            </ListItem>
          ))}
        </List>
      )}
    </Paper>
  );
};

import React from 'react';
import { render, screen, waitFor } from '@testing-library/react';
import { QueryClient, QueryClientProvider } from '@tanstack/react-query';
import { MemoryRouter, Route, Routes } from 'react-router';
import { MCPContextView } from '../MCPContextView';
import { useMCPContext } from '../useMCPContext';

// Mock del hook useMCPContext
jest.mock('../useMCPContext');
const mockUseMCPContext = useMCPContext as jest.MockedFunction<typeof useMCPContext>;

// Mock de i18next
jest.mock('react-i18next', () => ({
  useTranslation: () => ({
    t: (key: string) => key,
  }),
}));

// Datos de prueba
const mockContext = {
  patient_state: {
    age: 45,
    sex: 'M',
    history: ['HTA', 'DM2']
  },
  visit_metadata: {
    visit_id: '123',
    date: new Date().toISOString(),
    professional: 'dr.test@example.com'
  },
  rules_and_constraints: ['Regla 1', 'Regla 2'],
  system_instructions: 'Instrucciones de prueba',
  enrichment: {
    emr: {
      patient_data: {
        name: 'Juan P√©rez',
        allergies: ['Penicilina'],
        chronicConditions: ['HTA'],
        medications: ['Enalapril']
      },
      visit_history: []
    }
  }
};

describe('MCPContextView', () => {
  let queryClient: QueryClient;

  beforeEach(() => {
    queryClient = new QueryClient({
      defaultOptions: {
        queries: {
          retry: false,
        },
      },
    });
  });

  const renderWithProviders = (visitId?: string) => {
    return render(
      <QueryClientProvider client={queryClient}>
        <MemoryRouter initialEntries={[`/mcp/${visitId || '123'}`]}>
          <Routes>
            <Route path="/mcp/:id" element={<MCPContextView />} />
          </Routes>
        </MemoryRouter>
      </QueryClientProvider>
    );
  };

  it('muestra loader mientras carga', () => {
    mockUseMCPContext.mockReturnValue({
      data: undefined,
      isLoading: true,
      error: null,
    } as any);

    renderWithProviders();
    expect(screen.getByRole('progressbar')).toBeInTheDocument();
  });

  it('muestra error cuando falla la carga', () => {
    const error = new Error('Error de prueba');
    mockUseMCPContext.mockReturnValue({
      data: undefined,
      isLoading: false,
      error,
    } as any);

    renderWithProviders();
    expect(screen.getByRole('alert')).toHaveTextContent('Error de prueba');
  });

  it('muestra advertencia cuando no hay contexto', () => {
    mockUseMCPContext.mockReturnValue({
      data: null,
      isLoading: false,
      error: null,
    } as any);

    renderWithProviders();
    expect(screen.getByRole('alert')).toHaveTextContent('mcp.errors.not_found');
  });

  it('renderiza correctamente el contexto MCP', async () => {
    mockUseMCPContext.mockReturnValue({
      data: mockContext,
      isLoading: false,
      error: null,
    } as any);

    renderWithProviders();

    await waitFor(() => {
      expect(screen.getByText('Juan P√©rez')).toBeInTheDocument();
      expect(screen.getByText('45 a√±os')).toBeInTheDocument();
      expect(screen.getByText('Penicilina')).toBeInTheDocument();
      expect(screen.getByText('HTA')).toBeInTheDocument();
      expect(screen.getByText('Enalapril')).toBeInTheDocument();
    });
  });

  it('llama a onError cuando hay un error', () => {
    const onError = jest.fn();
    const error = new Error('Error de prueba');
    mockUseMCPContext.mockReturnValue({
      data: undefined,
      isLoading: false,
      error,
    } as any);

    render(<MCPContextView onError={onError} />);
    expect(onError).toHaveBeenCalledWith(error);
  });
});

import type { ReactNode } from 'react';
import type { MCPContext } from '../../../../core/mcp/CopilotContextBuilder';
import type { TestWrapperProps } from '@/types/testing';
import { renderHook, waitFor } from '@testing-library/react';
import { QueryClient, QueryClientProvider } from '@tanstack/react-query';
import axios from 'axios';
import { useMCPContext } from '../useMCPContext';
import { trackEvent } from '../../../core/lib/langfuse.client';

// Mock de axios
jest.mock('axios');
const mockAxios = axios as jest.Mocked<typeof axios>;

// Mock de trackEvent
jest.mock('../../../core/lib/langfuse.client');
const mockTrackEvent = trackEvent as jest.MockedFunction<typeof trackEvent>;

describe('useMCPContext', () => {
  let queryClient: QueryClient;

  beforeEach(() => {
    queryClient = new QueryClient({
      defaultOptions: {
        queries: {
          retry: false,
        },
      },
    });
    jest.clearAllMocks();
  });

  const wrapper = ({ children }: TestWrapperProps) => (
    <QueryClientProvider client={queryClient}>
      {children}
    </QueryClientProvider>
  );

  const mockContext: MCPContext = {
    patient_state: {
      age: 45,
      sex: 'M',
      history: ['HTA']
    },
    visit_metadata: {
      visit_id: '123',
      date: new Date().toISOString(),
      professional: 'test@example.com'
    },
