  /**
   * Divide un texto largo en l√≠neas que quepan en el ancho disponible
   */
  private static getTextLines(
    text: string,
    font: any,
    fontSize: number,
    maxWidth: number
  ): string[] {
    const words = text.split(' ');
    const lines: string[] = [];
    let currentLine = '';

    words.forEach(word => {
      // Manejar saltos de l√≠nea en el texto original
      if (word.includes('\n')) {
        const segments = word.split('\n');

        // Procesar primer segmento con la l√≠nea actual
        const testLine = currentLine + (currentLine ? ' ' : '') + segments[0];
        const width = font.widthOfTextAtSize(testLine, fontSize);

        if (width <= maxWidth) {
          currentLine = testLine;
        } else {
          lines.push(currentLine);
          currentLine = segments[0];
        }

        // Agregar la l√≠nea actual
        lines.push(currentLine);

        // Procesar los segmentos restantes (si hay)
        currentLine = '';
        for (let i = 1; i < segments.length; i++) {
          if (i === segments.length - 1) {
            // El √∫ltimo segmento se convierte en la l√≠nea actual
            currentLine = segments[i];
          } else {
            // Los segmentos intermedios son l√≠neas completas
            lines.push(segments[i]);
          }
        }
      } else {
        // Comportamiento normal para palabras sin saltos de l√≠nea
        const testLine = currentLine + (currentLine ? ' ' : '') + word;
        const width = font.widthOfTextAtSize(testLine, fontSize);

        if (width <= maxWidth) {
          currentLine = testLine;
        } else {
          lines.push(currentLine);
          currentLine = word;
        }
      }
    });

    // Agregar la √∫ltima l√≠nea si no est√° vac√≠a
    if (currentLine) {
      lines.push(currentLine);
    }

    return lines;
  }

  /**
   * Convierte el rol del sistema a un texto m√°s amigable
   */
  private static getRoleDisplay(role: string): string {
    const roleMap: Record<string, string> = {
      'admin': 'Administrador',
      'professional': 'Profesional M√©dico',
      'fisioterapeuta': 'Fisioterapeuta',
      'secretary': 'Secretario/a',
      'developer': 'Desarrollador'
    };

    return roleMap[role] || role;
  }

  /**
   * Verifica la firma digital de un documento PDF
   * @param visitId ID de la visita asociada al documento
   * @param pdfBytes Array de bytes del documento PDF
   * @returns Promise con el resultado de la verificaci√≥n
   */
  static async verifyDocumentSignature(
    visitId: string,
    pdfBytes: Uint8Array
  ): Promise<{ valid: boolean; signature?: any; error?: string }> {
    try {
      // Generar el hash del documento
      const hash = await DocumentSignatureService.generateHash(pdfBytes);

      // Verificar si existe una firma con ese hash
      return await DocumentSignatureService.verifySignature(visitId, hash);
    } catch (error) {
      console.error('Error al verificar la firma del documento:', error);
      const errorMessage = error instanceof Error ? error.message : 'Error desconocido';
      return { valid: false, error: errorMessage };
    }
  }
}

