export function getRandomFutureDate(minDays: number = 1, maxDays: number = 30): string {
  const now = new Date();
  const randomDays = Math.floor(Math.random() * (maxDays - minDays + 1)) + minDays;
  now.setDate(now.getDate() + randomDays);
  return now.toISOString();
}

