// Tipos
type EventType = 'form.update' | 'audio.review' | 'pdf.export' | 'mcp.context.build';

interface Professional {
  id: string;
  name: string;
  email: string;
  role?: string;
  created_at?: string;
}

interface EventSummary {
  count: number;
  lastActivity: Date | null;
  pendingAudit: number;
}

interface ProfessionalStats {
  totalVisits: number;
  events: Record<EventType, EventSummary>;
  incompleteVisits: number;
}

const ProfessionalAuditDashboard: React.FC = () => {
  const [professionals, setProfessionals] = useState<Professional[]>([]);
  const [selectedProId, setSelectedProId] = useState<string | null>(null);
  const [stats, setStats] = useState<ProfessionalStats | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [expandedSection, setExpandedSection] = useState<string | null>(null);

  // Cargar lista de profesionales desde Supabase
  useEffect(() => {
    const fetchProfessionals = async () => {
      try {
        const { data, error } = await supabase
          .from('users')
          .select('id, name, email, role, created_at')
          .order('name');

        if (error) {
          throw error;
        }

        setProfessionals(data || []);

        // Seleccionar el primer profesional por defecto si hay alguno
        if (data && data.length > 0 && !selectedProId) {
          setSelectedProId(data[0].id);
        }
      } catch (err: any) {
        console.error('Error fetching professionals:', err);
        setError('No se pudieron cargar los profesionales del sistema');
      } finally {
        setLoading(false);
      }
    };

    fetchProfessionals();
  }, []);

  // Cargar estad√≠sticas cuando se selecciona un profesional
  useEffect(() => {
    if (!selectedProId) return;

    const fetchUserStats = async () => {
      setLoading(true);
      setError(null);

      try {
        // Inicializar Langfuse
        const langfuse = new Langfuse({
          publicKey: process.env.LANGFUSE_PUBLIC_KEY || '',
          secretKey: process.env.LANGFUSE_SECRET_KEY || '',
          baseUrl: process.env.LANGFUSE_HOST || 'https://cloud.langfuse.com'
        });

        // Obtener trazas para este usuario
        const { data: traces } = await langfuse.trace.list({
          metadata: { userId: selectedProId },
          limit: 500, // Un n√∫mero razonable para analizar
        });

        // Si no hay trazas, intentar fallback local
        if (!traces || traces.length === 0) {
          await tryLoadLocalStats(selectedProId);
          return;
        }

        // Conjunto para seguir visitIds √∫nicos
        const visitIds = new Set<string>();

        // Contadores por tipo de evento
        const eventStats: Record<EventType, EventSummary> = {
          'form.update': { count: 0, lastActivity: null, pendingAudit: 0 },
          'audio.review': { count: 0, lastActivity: null, pendingAudit: 0 },
          'pdf.export': { count: 0, lastActivity: null, pendingAudit: 0 },
          'mcp.context.build': { count: 0, lastActivity: null, pendingAudit: 0 }
        };

        // Visitas sin completar (sin PDF o sin audio validado)
        const incompleteVisits = new Set<string>();

        // Procesar cada traza
        for (const trace of traces) {
          // Extraer visitId de los metadatos
          const visitId = trace.metadata?.visitId;
          if (visitId) {
            visitIds.add(visitId.toString());
          }

          // Obtener observaciones para esta traza
          const { data: observations } = await langfuse.observation.list({
            traceId: trace.id,
            limit: 50
          });

          // Procesar cada observaci√≥n
          if (observations) {
            for (const obs of observations) {
              const type = obs.type as EventType;

              // Solo contar tipos que nos interesan
              if (type && ['form.update', 'audio.review', 'pdf.export', 'mcp.context.build'].includes(type)) {
                // Incrementar contador
                eventStats[type].count++;

                // Actualizar √∫ltima actividad
                const startTime = new Date(obs.startTime || Date.now());
                if (!eventStats[type].lastActivity || startTime > eventStats[type].lastActivity) {
                  eventStats[type].lastActivity = startTime;
                }

                // Marcar visita como incompleta si el audio no est√° aprobado
                if (type === 'audio.review' && visitId && !obs.metadata?.approved) {
                  incompleteVisits.add(visitId.toString());
                }
              }
            }

            // Verificar si la visita no tiene exportaci√≥n PDF o review de audio
            if (visitId) {
              const hasPdfExport = observations.some(obs => obs.type === 'pdf.export');
              const hasAudioReview = observations.some(obs => obs.type === 'audio.review');

              if (!hasPdfExport || !hasAudioReview) {
                incompleteVisits.add(visitId.toString());
              }
            }
          }
        }

        // Actualizar estad√≠sticas
        setStats({
          totalVisits: visitIds.size,
          events: eventStats,
          incompleteVisits: incompleteVisits.size
        });
      } catch (err: any) {
        console.error('Error fetching user stats:', err);
        setError('No se pudieron cargar las estad√≠sticas del profesional');

        // Intentar fallback local
        await tryLoadLocalStats(selectedProId);
      } finally {
        setLoading(false);
      }
    };

    fetchUserStats();
  }, [selectedProId]);

  // Cargar estad√≠sticas locales como fallback
  const tryLoadLocalStats = async (userId: string): Promise<void> => {
    try {
      const response = await fetch(`/logs/user-${userId}.json`);
      if (!response.ok) throw new Error('No local logs available');

      const localLogs = await response.json();

      // Transformar logs locales a estad√≠sticas
      const visitIds = new Set<string>();
      const incompleteVisits = new Set<string>();

      const eventStats: Record<EventType, EventSummary> = {
        'form.update': { count: 0, lastActivity: null, pendingAudit: 0 },
        'audio.review': { count: 0, lastActivity: null, pendingAudit: 0 },
        'pdf.export': { count: 0, lastActivity: null, pendingAudit: 0 },
        'mcp.context.build': { count: 0, lastActivity: null, pendingAudit: 0 }
      };

      for (const log of localLogs) {
        const type = log.type as EventType;
        const visitId = log.visitId;

        if (visitId) {
          visitIds.add(visitId.toString());
        }

        if (type && ['form.update', 'audio.review', 'pdf.export', 'mcp.context.build'].includes(type)) {
          eventStats[type].count++;

          const timestamp = new Date(log.timestamp || Date.now());
          if (!eventStats[type].lastActivity || timestamp > eventStats[type].lastActivity) {
            eventStats[type].lastActivity = timestamp;
          }

          if (type === 'audio.review' && visitId && !log.details?.approved) {
            incompleteVisits.add(visitId.toString());
          }
        }
      }

      // Agregamos visitas sin PDF o sin audio
      for (const visitId of visitIds) {
        const hasAudio = localLogs.some(log => 
          log.visitId === visitId && log.type === 'audio.review');
        const hasPdf = localLogs.some(log => 
          log.visitId === visitId && log.type === 'pdf.export');

        if (!hasAudio || !hasPdf) {
          incompleteVisits.add(visitId);
        }
      }

      setStats({
        totalVisits: visitIds.size,
        events: eventStats,
        incompleteVisits: incompleteVisits.size
      });
    } catch (err) {
      console.error('Error loading local stats:', err);
      // Si tambi√©n fallan los logs locales, mostramos datos vac√≠os
      setStats({
        totalVisits: 0,
        events: {
          'form.update': { count: 0, lastActivity: null, pendingAudit: 0 },
          'audio.review': { count: 0, lastActivity: null, pendingAudit: 0 },
          'pdf.export': { count: 0, lastActivity: null, pendingAudit: 0 },
          'mcp.context.build': { count: 0, lastActivity: null, pendingAudit: 0 }
        },
        incompleteVisits: 0
      });
    }
  };

  // Cambiar profesional seleccionado
  const handleProfessionalChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    setSelectedProId(event.target.value);
  };

  // Manejar expansi√≥n de secciones
  const toggleSection = (section: string) => {
    setExpandedSection(expandedSection === section ? null : section);
  };

  // Renderizar selector de profesionales
  const renderProfessionalSelector = () => (
    <div className="mb-6">
      <label htmlFor="professional-select" className="block text-sm font-medium text-gray-700 mb-2">
        Seleccionar Profesional
      </label>
      <select
        id="professional-select"
        className="block w-full md:w-64 px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
        value={selectedProId || ''}
        onChange={handleProfessionalChange}
        disabled={loading || professionals.length === 0}
      >
        {professionals.length === 0 ? (
          <option value="">No hay profesionales disponibles</option>
        ) : (
          professionals.map(pro => (
            <option key={pro.id} value={pro.id}>
              {pro.name || pro.email}
            </option>
          ))
        )}
      </select>
    </div>
  );

  // Renderizar estad√≠sticas de usuario
  const renderUserStats = () => {
    if (!stats) return null;

    const eventIcons: Record<EventType, React.ReactNode> = {
      'form.update': <FileText className="w-5 h-5 text-blue-500" />,
      'audio.review': <Mic className="w-5 h-5 text-green-500" />,
      'pdf.export': <FilePdf className="w-5 h-5 text-red-500" />,
      'mcp.context.build': <Database className="w-5 h-5 text-purple-500" />
    };

    const eventTitles: Record<EventType, string> = {
      'form.update': 'Actualizaci√≥n de formularios',
      'audio.review': 'Revisi√≥n de audio',
      'pdf.export': 'Exportaci√≥n de PDF',
      'mcp.context.build': 'Creaci√≥n de contexto MCP'
    };

    return (
      <div className="space-y-6">
        {/* Visitas totales y visitas incompletas */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="bg-white p-6 rounded-lg shadow">
            <div className="flex items-center">
              <div className="rounded-md bg-blue-50 p-3 mr-4">
                <User className="h-6 w-6 text-blue-500" />
              </div>
              <div>
                <p className="text-sm font-medium text-gray-500">Total Visitas</p>
                <p className="text-2xl font-semibold text-gray-900">{stats.totalVisits}</p>
              </div>
            </div>
          </div>

          <div className="bg-white p-6 rounded-lg shadow">
            <div className="flex items-center">
              <div className={`rounded-md p-3 mr-4 ${stats.incompleteVisits > 0 ? 'bg-red-50' : 'bg-green-50'}`}>
                {stats.incompleteVisits > 0 ? (
                  <AlertCircle className="h-6 w-6 text-red-500" />
                ) : (
                  <Check className="h-6 w-6 text-green-500" />
                )}
              </div>
              <div>
                <p className="text-sm font-medium text-gray-500">Visitas Incompletas</p>
                <p className="text-2xl font-semibold text-gray-900">{stats.incompleteVisits}</p>
              </div>
            </div>
          </div>
        </div>

        {/* Estad√≠sticas por tipo de evento */}
        <div className="bg-white shadow overflow-hidden rounded-lg">
          <div className="px-4 py-5 sm:px-6">
            <h3 className="text-lg font-medium text-gray-900">Actividades por Tipo de Evento</h3>
            <p className="mt-1 max-w-2xl text-sm text-gray-500">
              Resumen de eventos cl√≠nicos registrados por este profesional
            </p>
          </div>

          <div className="border-t border-gray-200">
            <dl>
              {Object.entries(stats.events).map(([type, data], index) => {
                const eventType = type as EventType;
                return (
                  <div 
                    key={type}
                    className={`${index % 2 === 0 ? 'bg-gray-50' : 'bg-white'} px-4 py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6`}
                  >
                    <dt className="flex items-center text-sm font-medium text-gray-500">
                      {eventIcons[eventType]}
                      <span className="ml-2">{eventTitles[eventType]}</span>
                    </dt>
                    <dd className="mt-1 text-sm text-gray-900 sm:mt-0 sm:col-span-2">
                      <div className="flex justify-between items-center">
                        <span className="font-semibold">{data.count} eventos</span>
                        <span className="text-gray-500">
                          {data.lastActivity 
                            ? `√öltima actividad: ${new Date(data.lastActivity).toLocaleDateString('es-ES', {
                                day: '2-digit', month: 'short', year: 'numeric'
                              })}` 
                            : 'Sin actividad registrada'}
                        </span>
                      </div>
                    </dd>
                  </div>
                );
              })}
            </dl>
          </div>
        </div>

        {/* Secci√≥n de visitas incompletas */}
        {stats.incompleteVisits > 0 && (
          <div className="bg-white shadow overflow-hidden rounded-lg">
            <div 
              className="px-4 py-5 sm:px-6 flex justify-between items-center cursor-pointer"
              onClick={() => toggleSection('incomplete')}
            >
              <h3 className="text-lg font-medium text-gray-900">Auditor√≠as Incompletas</h3>
              {expandedSection === 'incomplete' ? (
                <ChevronUp className="h-5 w-5 text-gray-500" />
              ) : (
                <ChevronDown className="h-5 w-5 text-gray-500" />
              )}
            </div>

            {expandedSection === 'incomplete' && (
              <div className="border-t border-gray-200 px-4 py-5 sm:p-6">
                <div className="bg-yellow-50 p-4 rounded-md">
                  <div className="flex">
                    <div className="flex-shrink-0">
                      <AlertCircle className="h-5 w-5 text-yellow-400" aria-hidden="true" />
                    </div>
                    <div className="ml-3">
                      <h3 className="text-sm font-medium text-yellow-800">
                        Atenci√≥n requerida
                      </h3>
                      <div className="mt-2 text-sm text-yellow-700">
                        <p>
                          Este profesional tiene {stats.incompleteVisits} visitas que requieren acciones adicionales:
                        </p>
                        <ul className="list-disc pl-5 mt-1 space-y-1">
                          <li>Visitas sin validaci√≥n de audio</li>
                          <li>Visitas sin exportaci√≥n a PDF</li>
                        </ul>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="bg-gray-100 rounded-lg p-6">
      <h2 className="text-2xl font-bold mb-6">Auditor√≠a por Profesional</h2>

      {/* Selector de profesionales */}
      {renderProfessionalSelector()}

      {/* Cargando o error */}
      {loading ? (
        <div className="flex justify-center items-center h-40" role="status">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
        </div>
      ) : error ? (
        <div className="bg-red-50 border border-red-200 text-red-800 px-4 py-3 rounded">
          {error}
        </div>
      ) : (
        renderUserStats()
      )}
    </div>
  );
};

export default ProfessionalAuditDashboard;

import { useState, useEffect  } from 'react';
import { Langfuse } from 'langfuse';
import { supabase } from '@/core/lib/supabase';
import { AlertCircle, Calendar, Filter, User, CheckCircle, XCircle, FileText, Mic, File as FilePdf, Database, ChevronDown, ChevronUp } from 'lucide-react';
import { format, subWeeks, subMonths, startOfWeek, endOfWeek, startOfMonth, endOfMonth, isWithinInterval } from 'date-fns';
import { es } from 'date-fns/locale';

// Tipos de eventos
type EventType = 'form.update' | 'audio.review' | 'pdf.export' | 'mcp.context.build';

// Tipos de omisiones cr√≠ticas
type OmissionType = 'checklist' | 'signature' | 'mcp' | 'export';

// Per√≠odos de tiempo para agrupaci√≥n
type TimePeriod = 'current-week' | 'last-week' | 'current-month' | 'all';

// Estado para cada validaci√≥n
interface ValidationStatus {
  checked: boolean;
  passed: boolean;
}

// Estructura para una visita con informaci√≥n de riesgo
interface RiskVisit {
  id: string;
  date: Date;
  patientId: string;
  patientName: string;
  professionalId: string;
  professionalName: string;
  validations: {
    checklist: ValidationStatus;  // audio.review + approved
    signature: ValidationStatus;  // pdf.export + signed
    mcp: ValidationStatus;        // mcp.context.build
    export: ValidationStatus;     // pdf.export exists
  };
  riskLevel: 'high' | 'medium' | 'low';
  omissionCount: number;
}

// Agrupaci√≥n de visitas por profesional
interface GroupedVisits {
  [professionalId: string]: RiskVisit[];
}

// Props para el dashboard
interface RiskMonitorDashboardProps {
  className?: string;
}

const RiskMonitorDashboard: React.FC<RiskMonitorDashboardProps> = ({ className }) => {
  // Estado para almacenar las visitas con riesgos
  const [visits, setVisits] = useState<RiskVisit[]>([]);

  // Estados para filtros y agrupaciones
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedProfessional, setSelectedProfessional] = useState<string>('all');
  const [selectedPeriod, setSelectedPeriod] = useState<TimePeriod>('current-week');
  const [selectedOmissions, setSelectedOmissions] = useState<OmissionType[]>([]);
  const [dateRange, setDateRange] = useState<{ start: Date | null; end: Date | null }>({ start: null, end: null });

  // Estados para la UI
  const [professionals, setProfessionals] = useState<{ id: string; name: string }[]>([]);
  const [expandedGroups, setExpandedGroups] = useState<Record<string, boolean>>({});
  const [groupedVisits, setGroupedVisits] = useState<GroupedVisits>({});

  // Funci√≥n para calcular el rango de fechas basado en el per√≠odo seleccionado
  useEffect(() => {
    const now = new Date();
    let start = null;
    let end = null;

    switch (selectedPeriod) {
      case 'current-week':
        start = startOfWeek(now, { locale: es });
        end = endOfWeek(now, { locale: es });
        break;
      case 'last-week':
        start = startOfWeek(subWeeks(now, 1), { locale: es });
        end = endOfWeek(subWeeks(now, 1), { locale: es });
        break;
      case 'current-month':
        start = startOfMonth(now);
        end = endOfMonth(now);
        break;
      case 'all':
        start = null;
        end = null;
        break;
    }

    setDateRange({ start, end });
  }, [selectedPeriod]);

  // Cargar profesionales desde Supabase
  useEffect(() => {
    const fetchProfessionals = async () => {
      try {
        const { data, error } = await supabase
          .from('users')
          .select('id, name')
          .order('name');

        if (error) throw error;

        setProfessionals(data || []);
      } catch (err) {
        console.error('Error fetching professionals:', err);
        setError('No se pudieron cargar los profesionales');
      }
    };

    fetchProfessionals();
  }, []);

  // Cargar visitas con riesgos
  useEffect(() => {
    const fetchRiskyVisits = async () => {
      setLoading(true);
      setError(null);

      try {
        // Inicializar cliente de Langfuse
        const langfuse = new Langfuse({
          publicKey: process.env.LANGFUSE_PUBLIC_KEY || '',
          secretKey: process.env.LANGFUSE_SECRET_KEY || '',
          baseUrl: process.env.LANGFUSE_HOST || 'https://cloud.langfuse.com'
        });

        // Obtener trazas recientes (con un l√≠mite razonable)
        const { data: traces } = await langfuse.trace.list({
          limit: 500, // Un n√∫mero razonable para analizar
        });

        if (!traces || traces.length === 0) {
          await tryLoadLocalVisits();
          return;
        }

        // Mapeo para guardar informaci√≥n sobre visitas
        const visitsMap = new Map<string, {
          visitId: string;
          patientId?: string;
          patientName?: string;
          professionalId?: string;
          professionalName?: string;
          date: Date;
          hasChecklist: boolean;
          checklistApproved: boolean;
          hasPdfExport: boolean;
          pdfSigned: boolean;
          hasMcpContext: boolean;
        }>();

        // Procesar cada traza para extraer informaci√≥n sobre visitas
        for (const trace of traces) {
          const visitId = trace.metadata?.visitId;
          const patientId = trace.metadata?.patientId;
          const userId = trace.metadata?.userId;

          if (!visitId) continue;

          // Determinar la fecha de la visita
          const visitDate = trace.metadata?.visitDate
            ? new Date(trace.metadata.visitDate)
            : new Date(trace.startTime || Date.now());

          // Inicializar visita si no existe
          if (!visitsMap.has(visitId.toString())) {
            visitsMap.set(visitId.toString(), {
              visitId: visitId.toString(),
              patientId: patientId?.toString(),
              professionalId: userId?.toString(),
              date: visitDate,
              hasChecklist: false,
              checklistApproved: false,
              hasPdfExport: false,
              pdfSigned: false,
              hasMcpContext: false,
            });
          }

          // Obtener observaciones para cada traza
          const { data: observations } = await langfuse.observation.list({
            traceId: trace.id,
            limit: 50
          });

          if (!observations || observations.length === 0) continue;

          // Procesar observaciones para actualizar estado de la visita
          const visit = visitsMap.get(visitId.toString());
          if (!visit) continue;

          for (const obs of observations) {
            switch (obs.type) {
              case 'audio.review':
                visit.hasChecklist = true;
                visit.checklistApproved = !!obs.metadata?.approved;
                break;
              case 'pdf.export':
                visit.hasPdfExport = true;
                visit.pdfSigned = !!obs.metadata?.signed;
                break;
              case 'mcp.context.build':
                visit.hasMcpContext = true;
                break;
            }
          }
        }

        // Convertir a formato RiskVisit y calcular nivel de riesgo
        const riskyVisits: RiskVisit[] = [];

        // Cargar nombres de pacientes y profesionales
        const patientIds = new Set<string>();
        const professionalIds = new Set<string>();

        visitsMap.forEach(visit => {
          if (visit.patientId) patientIds.add(visit.patientId);
          if (visit.professionalId) professionalIds.add(visit.professionalId);
        });

        // Obtener nombres de pacientes
        const patientNames = await fetchPatientNames(Array.from(patientIds));
        const professionalNames = await fetchProfessionalNames(Array.from(professionalIds));

        // Convertir visitas y calcular riesgos
        visitsMap.forEach(visit => {
          // Verificar si la visita est√° en el rango de fechas seleccionado
          if (dateRange.start && dateRange.end) {
            if (!isWithinInterval(visit.date, { start: dateRange.start, end: dateRange.end })) {
              return; // No incluir esta visita
            }
          }

          // Calcular validaciones
          const validations = {
            checklist: { checked: visit.hasChecklist, passed: visit.checklistApproved },
            export: { checked: true, passed: visit.hasPdfExport },
            signature: { checked: visit.hasPdfExport, passed: visit.pdfSigned },
            mcp: { checked: true, passed: visit.hasMcpContext }
          };

          // Contar omisiones
          const omissionCount = [
            !validations.checklist.passed,
            !validations.export.passed, 
            !validations.signature.passed,
            !validations.mcp.passed
          ].filter(Boolean).length;

          // Determinar nivel de riesgo
          let riskLevel: 'high' | 'medium' | 'low' = 'low';
          if (omissionCount >= 3) {
            riskLevel = 'high';
          } else if (omissionCount >= 1) {
            riskLevel = 'medium';
          }

          // Solo incluir visitas con alguna omisi√≥n
          if (omissionCount > 0) {
            riskyVisits.push({
              id: visit.visitId,
              date: visit.date,
              patientId: visit.patientId || 'unknown',
              patientName: patientNames[visit.patientId || ''] || 'Paciente desconocido',
              professionalId: visit.professionalId || 'unknown',
              professionalName: professionalNames[visit.professionalId || ''] || 'Profesional desconocido',
              validations,
              riskLevel,
              omissionCount
            });
          }
        });

        // Ordenar por fecha (m√°s reciente primero) y nivel de riesgo
        riskyVisits.sort((a, b) => {
          // Primero por nivel de riesgo
          const riskOrder = { high: 0, medium: 1, low: 2 };
          const riskDiff = riskOrder[a.riskLevel] - riskOrder[b.riskLevel];
          if (riskDiff !== 0) return riskDiff;

          // Luego por fecha
          return b.date.getTime() - a.date.getTime();
        });

        setVisits(riskyVisits);

        // Agrupar por profesional
        const grouped: GroupedVisits = {};
        riskyVisits.forEach(visit => {
          if (!grouped[visit.professionalId]) {
            grouped[visit.professionalId] = [];
          }
          grouped[visit.professionalId].push(visit);
        });

        setGroupedVisits(grouped);
      } catch (err) {
        console.error('Error fetching risky visits:', err);
        setError('Error al cargar las visitas de riesgo');

        // Intentar cargar desde logs locales
        await tryLoadLocalVisits();
      } finally {
        setLoading(false);
      }
    };

    fetchRiskyVisits();
  }, [dateRange]);

  // Funci√≥n para obtener nombres de pacientes
  const fetchPatientNames = async (patientIds: string[]): Promise<Record<string, string>> => {
    if (patientIds.length === 0) return {};

    try {
      const { data } = await supabase
        .from('patients')
        .select('id, name')
        .in('id', patientIds);

      return (data || []).reduce((acc, patient) => {
        acc[patient.id] = patient.name;
        return acc;
      }, {} as Record<string, string>);
    } catch (err) {
      console.error('Error fetching patient names:', err);
      return {};
    }
  };

  // Funci√≥n para obtener nombres de profesionales
  const fetchProfessionalNames = async (professionalIds: string[]): Promise<Record<string, string>> => {
    if (professionalIds.length === 0) return {};

    try {
      const { data } = await supabase
        .from('users')
        .select('id, name')
        .in('id', professionalIds);

      return (data || []).reduce((acc, user) => {
        acc[user.id] = user.name;
        return acc;
      }, {} as Record<string, string>);
    } catch (err) {
      console.error('Error fetching professional names:', err);
      return {};
    }
  };

  // Cargar visitas desde logs locales (fallback)
  const tryLoadLocalVisits = async (): Promise<void> => {
    try {
      // Intento cargar un archivo general de visitas de riesgo
      const response = await fetch('/logs/risky-visits.json');
      if (!response.ok) throw new Error('No local logs available');

      const localVisits = await response.json();

      // Transformar a formato RiskVisit
      const riskyVisits: RiskVisit[] = localVisits.map((visit: any) => ({
        ...visit,
        date: new Date(visit.date),
        validations: {
          checklist: { 
            checked: true, 
            passed: visit.validations?.checklist?.passed || false 
          },
          export: { 
            checked: true, 
            passed: visit.validations?.export?.passed || false 
          },
          signature: { 
            checked: true, 
            passed: visit.validations?.signature?.passed || false 
          },
          mcp: { 
            checked: true, 
            passed: visit.validations?.mcp?.passed || false 
          }
        }
      }));

      // Aplicar filtros de fecha
      const filteredVisits = dateRange.start && dateRange.end 
        ? riskyVisits.filter(visit => 
            isWithinInterval(visit.date, { start: dateRange.start!, end: dateRange.end! })
          )
        : riskyVisits;

      setVisits(filteredVisits);

      // Agrupar por profesional
      const grouped: GroupedVisits = {};
      filteredVisits.forEach(visit => {
        if (!grouped[visit.professionalId]) {
          grouped[visit.professionalId] = [];
        }
        grouped[visit.professionalId].push(visit);
      });

      setGroupedVisits(grouped);
    } catch (err) {
      console.error('Error loading local visits:', err);
      setVisits([]);
      setGroupedVisits({});
    }
  };

  // Cambiar filtro de per√≠odo
  const handlePeriodChange = (period: TimePeriod) => {
    setSelectedPeriod(period);
  };

  // Cambiar filtro de profesional
  const handleProfessionalChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    setSelectedProfessional(event.target.value);
  };

  // Manejar cambios en los filtros de omisiones
  const handleOmissionFilterChange = (omission: OmissionType) => {
    setSelectedOmissions(prev => 
      prev.includes(omission) 
        ? prev.filter(o => o !== omission)
        : [...prev, omission]
    );
  };

  // Alternar expansi√≥n de un grupo
  const toggleGroupExpansion = (groupId: string) => {
    setExpandedGroups(prev => ({
      ...prev,
      [groupId]: !prev[groupId]
    }));
  };

  // Filtrar visitas seg√∫n los criterios seleccionados
  const getFilteredVisits = (): RiskVisit[] => {
    let filtered = [...visits];

    // Filtrar por profesional
    if (selectedProfessional !== 'all') {
      filtered = filtered.filter(visit => visit.professionalId === selectedProfessional);
    }

    // Filtrar por omisiones
    if (selectedOmissions.length > 0) {
      filtered = filtered.filter(visit => 
        selectedOmissions.some(omission => !visit.validations[omission].passed)
      );
    }

    return filtered;
  };

  // Componente para mostrar una validaci√≥n de riesgo
  const ValidationIndicator: React.FC<{ 
    type: OmissionType,
    status: ValidationStatus
  }> = ({ type, status }) => {
    // Iconos y texto por tipo
    const config = {
      checklist: {
        icon: <Mic className="w-5 h-5" />,
        label: 'Checklist',
        tooltip: status.passed ? 'Audio validado' : 'Checklist no validado'
      },
      export: {
        icon: <FilePdf className="w-5 h-5" />,
        label: 'Exportaci√≥n',
        tooltip: status.passed ? 'PDF exportado' : 'Sin exportaci√≥n PDF'
      },
      signature: {
        icon: <FileText className="w-5 h-5" />,
        label: 'Firma',
        tooltip: status.passed ? 'Documento firmado' : 'Sin firma digital'
      },
      mcp: {
        icon: <Database className="w-5 h-5" />,
        label: 'MCP',
        tooltip: status.passed ? 'Contexto MCP creado' : 'Sin contexto MCP'
      }
    };

    // Colores seg√∫n el estado
    const colorClass = status.passed 
      ? 'text-green-500 bg-green-50'
      : 'text-red-500 bg-red-50';

    return (
      <div className={`flex items-center px-3 py-1 rounded-full ${colorClass}`} title={config[type].tooltip}>
        <div className="mr-1">
          {config[type].icon}
        </div>
        <span className="text-xs font-medium">{config[type].label}</span>
        {status.passed 
          ? <CheckCircle className="w-4 h-4 ml-1" /> 
          : <XCircle className="w-4 h-4 ml-1" />
        }
      </div>
    );
  };

  // Componente para indicador de nivel de riesgo
  const RiskLevelIndicator: React.FC<{ level: 'high' | 'medium' | 'low', count: number }> = ({ level, count }) => {
    const config = {
      high: {
        bgColor: 'bg-red-100',
        textColor: 'text-red-800',
        label: 'Alto'
      },
      medium: {
        bgColor: 'bg-yellow-100',
        textColor: 'text-yellow-800',
        label: 'Medio'
      },
      low: {
        bgColor: 'bg-green-100',
        textColor: 'text-green-800',
        label: 'Bajo'
      }
    };

    return (
      <div className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${config[level].bgColor} ${config[level].textColor}`}>
        <AlertCircle className="w-3 h-3 mr-1" />
        <span>{config[level].label}</span>
        <span className="ml-1">({count})</span>
      </div>
    );
  };

  // Renderizar la secci√≥n de filtros
  const renderFilters = () => (
    <div className="bg-gray-50 p-4 rounded-lg mb-6">
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4">
        <div>
          <h3 className="text-sm font-medium text-gray-700 mb-2">Filtros</h3>
          <div className="flex flex-wrap gap-2">
            {/* Filtro por per√≠odo */}
            <div className="flex items-center space-x-2">
              <Calendar className="w-4 h-4 text-gray-500" />
              <select 
                aria-label="Filtrar por per√≠odo de tiempo"
                className="text-sm border-gray-300 rounded-md shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50"
                value={selectedPeriod}
                onChange={(e) => handlePeriodChange(e.target.value as TimePeriod)}
              >
                <option value="current-week">Esta semana</option>
                <option value="last-week">Semana anterior</option>
                <option value="current-month">Este mes</option>
                <option value="all">Todo el historial</option>
              </select>
            </div>

            {/* Filtro por profesional */}
            <div className="flex items-center space-x-2">
              <User className="w-4 h-4 text-gray-500" />
              <select 
                aria-label="Filtrar por profesional"
                className="text-sm border-gray-300 rounded-md shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50"
                value={selectedProfessional}
                onChange={handleProfessionalChange}
              >
                <option value="all">Todos los profesionales</option>
                {professionals.map(pro => (
                  <option key={pro.id} value={pro.id}>{pro.name}</option>
                ))}
              </select>
            </div>
          </div>
        </div>

        {/* Filtros por omisi√≥n */}
        <div>
          <h3 className="text-sm font-medium text-gray-700 mb-2">Tipo de omisi√≥n</h3>
          <div className="flex flex-wrap gap-2">
            <button
              className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium transition-colors ${
                selectedOmissions.includes('checklist') 
