import { useState, useEffect  } from 'react';
import { useParams } from 'react-router-dom';
import { Langfuse } from 'langfuse';
import { supabase } from '@/core/lib/supabaseClient';
import { FileText, Mic, File as FilePdf, Database, Clock, ChevronDown, ChevronUp, User } from 'lucide-react';
import { format } from 'date-fns';
import { es } from 'date-fns/locale';

// Tipos de eventos que manejamos
type EventType = 'form.update' | 'audio.review' | 'pdf.export' | 'mcp.context.build';

// Estructura para un evento de log
interface LogEvent {
  id: string;
  timestamp: Date;
  type: EventType;
  userId?: string;
  userName?: string;
  result: string;
  details?: Record<string, any>;
}

// Mapa de iconos por tipo de evento
const eventIcons: Record<EventType, React.ReactNode> = {
  'form.update': <FileText className="w-5 h-5 text-blue-500" />,
  'audio.review': <Mic className="w-5 h-5 text-green-500" />,
  'pdf.export': <FilePdf className="w-5 h-5 text-red-500" />,
  'mcp.context.build': <Database className="w-5 h-5 text-purple-500" />
};

// T√≠tulos descriptivos por tipo de evento
const eventTitles: Record<EventType, string> = {
  'form.update': 'Actualizaci√≥n de formulario cl√≠nico',
  'audio.review': 'Revisi√≥n de audio',
  'pdf.export': 'Exportaci√≥n de PDF',
  'mcp.context.build': 'Creaci√≥n de contexto MCP'
};

// Componente para la chip de tipo de evento
const EventTypeChip: React.FC<{ type: EventType }> = ({ type }) => {
  const bgColors: Record<EventType, string> = {
    'form.update': 'bg-blue-100 text-blue-800',
    'audio.review': 'bg-green-100 text-green-800',
    'pdf.export': 'bg-red-100 text-red-800',
    'mcp.context.build': 'bg-purple-100 text-purple-800'
  };

  return (
    <span className={`px-2 py-1 rounded-full text-xs font-medium ${bgColors[type]}`}>
      {type}
    </span>
  );
};

const VisitLogDashboard: React.FC = () => {
  const { id: visitId } = useParams<{ id: string }>();
  const [events, setEvents] = useState<LogEvent[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [collapsedGroups, setCollapsedGroups] = useState<Record<EventType, boolean>>({
    'form.update': false,
    'audio.review': false,
    'pdf.export': false,
    'mcp.context.build': false
  });

  // Cargar los eventos de Langfuse
  useEffect(() => {
    const fetchEvents = async () => {
      if (!visitId) return;

      setLoading(true);
      try {
        // Inicializar cliente de Langfuse
        const langfuse = new Langfuse({
          publicKey: process.env.LANGFUSE_PUBLIC_KEY || '',
          secretKey: process.env.LANGFUSE_SECRET_KEY || '',
          baseUrl: process.env.LANGFUSE_HOST || 'https://cloud.langfuse.com'
        });

        // Obtener trazas relacionadas con la visita
        const { data: traces } = await langfuse.trace.list({
          metadata: { visitId },
          limit: 100,
        });

        // Transformar trazas a nuestro formato de eventos
        let fetchedEvents: LogEvent[] = [];

        if (traces && traces.length > 0) {
          for (const trace of traces) {
            // Obtener observaciones para esta traza
            const { data: observations } = await langfuse.observation.list({
              traceId: trace.id,
              limit: 50
            });

            // Filtrar por tipos de eventos que nos interesan
            const filteredObservations = observations.filter(obs => 
              ['form.update', 'audio.review', 'pdf.export', 'mcp.context.build'].includes(obs.type ?? '')
            );

            // Mapear a nuestro formato
            const mappedEvents = await Promise.all(filteredObservations.map(async (event) => {
              let userName = undefined;

              // Si hay userId, buscar datos del usuario en Supabase
              if (event.metadata?.userId) {
                const { data } = await supabase
                  .from('users')
                  .select('name, email')
                  .eq('id', event.metadata.userId)
                  .single();

                if (data) {
                  userName = data.name || data.email;
                }
              }

              return {
                id: event.id,
                timestamp: new Date(event.startTime || Date.now()),
                type: event.type as EventType,
                userId: event.metadata?.userId,
                userName,
                result: getEventResultSummary(event),
                details: event.metadata
              };
            }));

            fetchedEvents = [...fetchedEvents, ...mappedEvents];
          }
        } else {
          // Fallback a logs locales
          await tryLoadLocalLogs(visitId);
        }

        // Ordenar por timestamp (m√°s reciente primero)
        fetchedEvents.sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime());

        setEvents(fetchedEvents);
      } catch (err) {
        console.error('Error fetching logs:', err);
        setError('No se pudieron cargar los logs de la visita');

        // Intentar cargar logs locales como fallback
        await tryLoadLocalLogs(visitId);
      } finally {
        setLoading(false);
      }
    };

    fetchEvents();
  }, [visitId]);

  // Funci√≥n para cargar logs locales si fallan los de Langfuse
  const tryLoadLocalLogs = async (visitId: string): Promise<void> => {
    try {
      const response = await fetch(`/logs/visit-${visitId}.json`);
      if (response.ok) {
        const localLogs = await response.json();

        // Transformar logs locales a nuestro formato
        const localEvents = localLogs.map((log: any) => ({
          id: log.id || `local-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
          timestamp: new Date(log.timestamp || Date.now()),
          type: log.type as EventType,
          userId: log.userId,
          userName: log.userName,
          result: log.message || 'Sin detalles disponibles',
          details: log.details || {}
        }));

        setEvents(localEvents);
      }
    } catch (err) {
      console.error('Error loading local logs:', err);
      // Si tambi√©n fallan los logs locales, dejamos el error original
    }
  };

  // Funci√≥n para obtener un resumen del resultado del evento
  const getEventResultSummary = (event: any): string => {
    switch (event.type) {
      case 'form.update':
        const fieldsCount = Object.keys(event.metadata?.fields || {}).length;
        return `${fieldsCount} campo${fieldsCount !== 1 ? 's' : ''} actualizado${fieldsCount !== 1 ? 's' : ''}`;

      case 'audio.review':
        return event.metadata?.approved 
          ? 'Audio revisado y aprobado' 
          : 'Audio revisado - requiere cambios';

      case 'pdf.export':
        return event.metadata?.signed 
          ? 'PDF exportado y firmado digitalmente' 
          : 'PDF exportado sin firma';

      case 'mcp.context.build':
        return `Contexto MCP creado: ${event.metadata?.contextSize || 0} elementos`;

      default:
        return event.metadata?.message || 'Evento registrado';
    }
  };

  // Obtener eventos agrupados por tipo
  const getEventsByType = (type: EventType): LogEvent[] => {
    return events.filter(event => event.type === type);
  };

  // Manejar colapso/expansi√≥n de grupos
  const toggleGroup = (type: EventType) => {
    setCollapsedGroups(prev => ({
      ...prev,
      [type]: !prev[type]
    }));
  };

  // Renderizar cronolog√≠a de eventos
  const renderEventTimeline = () => {
    const eventTypes: EventType[] = ['form.update', 'audio.review', 'pdf.export', 'mcp.context.build'];

    return (
      <div className="space-y-6">
        {eventTypes.map((type) => {
          const typeEvents = getEventsByType(type);
          const eventCount = typeEvents.length;

          if (eventCount === 0) return null;

          return (
            <div key={type} className="border rounded-lg shadow-sm overflow-hidden">
              <div 
                className="bg-gray-50 p-4 flex justify-between items-center cursor-pointer hover:bg-gray-100"
                onClick={() => toggleGroup(type)}
              >
                <div className="flex items-center space-x-3">
                  {eventIcons[type]}
                  <h3 className="font-medium">{eventTitles[type]}</h3>
                  <span className="bg-gray-200 text-gray-800 text-xs font-medium px-2.5 py-0.5 rounded-full">
                    {eventCount}
                  </span>
                </div>
                {collapsedGroups[type] ? <ChevronDown className="w-5 h-5" /> : <ChevronUp className="w-5 h-5" />}
              </div>

              {!collapsedGroups[type] && (
                <div className="divide-y divide-gray-200">
                  {typeEvents.map((event) => (
                    <div key={event.id} className="p-4 hover:bg-gray-50">
                      <div className="flex justify-between items-start">
                        <div className="flex items-start space-x-3">
                          <EventTypeChip type={event.type} />
                          <div>
                            <p className="font-medium text-gray-900">{event.result}</p>

                            {event.userName && (
                              <div className="flex items-center text-sm text-gray-500 mt-1">
                                <User className="w-4 h-4 mr-1" />
                                <span>{event.userName}</span>
                              </div>
                            )}
                          </div>
                        </div>

                        <div className="flex items-center text-sm text-gray-500">
                          <Clock className="w-4 h-4 mr-1" />
                          <span title={event.timestamp.toISOString()}>
                            {format(event.timestamp, 'dd MMM yyyy, HH:mm', { locale: es })}
                          </span>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          );
        })}
      </div>
    );
  };

  return (
    <div className="bg-white rounded-lg p-6">
      <h2 className="text-2xl font-bold mb-6">Registro de Actividad</h2>

      {loading ? (
        <div className="flex justify-center items-center h-40" role="status">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
        </div>
      ) : error ? (
        <div className="bg-red-50 border border-red-200 text-red-800 px-4 py-3 rounded">
          {error}
        </div>
      ) : events.length === 0 ? (
        <div className="bg-gray-50 border border-gray-200 text-gray-800 px-4 py-6 rounded text-center">
          No hay eventos registrados para esta visita
        </div>
      ) : (
        renderEventTimeline()
      )}
    </div>
  );
};

export default VisitLogDashboard;

import { useState, useEffect  } from 'react';
import { useParams } from 'react-router-dom';
import { Langfuse } from 'langfuse';
import { supabase } from '@/core/lib/supabase';
import { FileText, Mic, File as FilePdf, Database, Calendar, CheckCircle, XCircle, AlertCircle, ChevronDown, ChevronUp } from 'lucide-react';
import { format } from 'date-fns';
import { es } from 'date-fns/locale';

// Tipos
type EventType = 'form.update' | 'audio.review' | 'pdf.export' | 'mcp.context.build';

interface Patient {
  id: string;
  name: string;
  birthDate?: string;
  email?: string;
  gender?: string;
}

interface VisitSummary {
  id: string;
  date: Date;
  hasAudioReview: boolean;
  isAudioApproved: boolean;
  hasPdfExport: boolean;
  isPdfSigned: boolean;
  hasMcpContext: boolean;
  events: EventSummary[];
}

interface EventSummary {
  id: string;
  type: EventType;
  timestamp: Date;
  details?: Record<string, any>;
}

// Agrupar visitas por a√±o
interface VisitsByYear {
  [year: string]: VisitSummary[];
}

const PatientAuditDashboard: React.FC = () => {
  const { patientId } = useParams<{ patientId: string }>();
  const [patient, setPatient] = useState<Patient | null>(null);
  const [visitsByYear, setVisitsByYear] = useState<VisitsByYear>({});
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [expandedVisits, setExpandedVisits] = useState<Record<string, boolean>>({});
  const [expandedYears, setExpandedYears] = useState<Record<string, boolean>>({});

  // Cargar datos del paciente
  useEffect(() => {
    const fetchPatientData = async () => {
      if (!patientId) return;

      setLoading(true);
      setError(null);

      try {
        // Obtener datos b√°sicos del paciente
        const { data: patientData, error: patientError } = await supabase
          .from('patients')
          .select('id, name, birthDate, email, gender')
          .eq('id', patientId)
          .single();

        if (patientError) throw patientError;

        if (!patientData) {
          setError('No se encontr√≥ el paciente solicitado');
          setLoading(false);
          return;
        }

        setPatient(patientData);

        // Inicializar Langfuse
        const langfuse = new Langfuse({
          publicKey: process.env.LANGFUSE_PUBLIC_KEY || '',
          secretKey: process.env.LANGFUSE_SECRET_KEY || '',
          baseUrl: process.env.LANGFUSE_HOST || 'https://cloud.langfuse.com'
        });

        // Obtener trazas relacionadas con el paciente
        const { data: traces } = await langfuse.trace.list({
          metadata: { patientId },
          limit: 500, // Un n√∫mero razonable para historial
        });

        // Si no hay trazas, intentar fallback local
        if (!traces || traces.length === 0) {
          await tryLoadLocalVisits(patientId);
          return;
        }

        // Procesar visitas desde las trazas
        const visitsMap = new Map<string, VisitSummary>();

        for (const trace of traces) {
          // Extraer visitId de los metadatos
          const visitId = trace.metadata?.visitId;
          if (!visitId) continue;

          // Obtener fecha de la visita (del metadato o de la traza)
          const visitDate = trace.metadata?.visitDate
            ? new Date(trace.metadata.visitDate)
            : new Date(trace.startTime || Date.now());

          // Inicializar visita si no existe
          if (!visitsMap.has(visitId.toString())) {
            visitsMap.set(visitId.toString(), {
              id: visitId.toString(),
              date: visitDate,
              hasAudioReview: false,
              isAudioApproved: false,
              hasPdfExport: false,
              isPdfSigned: false,
              hasMcpContext: false,
              events: []
            });
          }

          // Obtener observaciones para esta traza
          const { data: observations } = await langfuse.observation.list({
            traceId: trace.id,
            limit: 100
          });

          if (!observations) continue;

          // Procesar observaciones
          for (const obs of observations) {
            if (!obs.type) continue;

            const type = obs.type as EventType;
            const visitSummary = visitsMap.get(visitId.toString());

            if (!visitSummary) continue;

            // Guardar evento
            if (['form.update', 'audio.review', 'pdf.export', 'mcp.context.build'].includes(type)) {
              visitSummary.events.push({
                id: obs.id,
                type: type,
                timestamp: new Date(obs.startTime || Date.now()),
                details: obs.metadata
              });

              // Actualizar flags espec√≠ficos
              switch (type) {
                case 'audio.review':
                  visitSummary.hasAudioReview = true;
                  visitSummary.isAudioApproved = !!obs.metadata?.approved;
                  break;
                case 'pdf.export':
                  visitSummary.hasPdfExport = true;
                  visitSummary.isPdfSigned = !!obs.metadata?.signed;
                  break;
                case 'mcp.context.build':
                  visitSummary.hasMcpContext = true;
                  break;
              }
            }
          }
        }

        // Agrupar visitas por a√±o
        const groupedVisits: VisitsByYear = {};

        // Convertir el Map a Array, ordenarlo por fecha y agrupar
        Array.from(visitsMap.values())
          .sort((a, b) => b.date.getTime() - a.date.getTime())
          .forEach(visit => {
            const year = visit.date.getFullYear().toString();
            if (!groupedVisits[year]) {
              groupedVisits[year] = [];
              // Expandir el a√±o actual por defecto
              setExpandedYears(prev => ({ ...prev, [year]: true }));
            }
            groupedVisits[year].push(visit);
          });

        setVisitsByYear(groupedVisits);
      } catch (err: any) {
        console.error('Error fetching patient data:', err);
        setError('Error al cargar el historial del paciente');

        // Intentar fallback local
        if (patientId) {
          await tryLoadLocalVisits(patientId);
        }
      } finally {
        setLoading(false);
      }
    };

    fetchPatientData();
  }, [patientId]);

  // Cargar visitas locales como fallback
  const tryLoadLocalVisits = async (patientId: string): Promise<void> => {
    try {
      // Primero intentamos cargar los datos del paciente
      const patientResponse = await fetch(`/logs/patient-${patientId}.json`);
      if (patientResponse.ok) {
        const patientData = await patientResponse.json();
        setPatient(patientData);
      }

      // Luego cargamos las visitas de logs locales
      const response = await fetch(`/logs/patient-visits-${patientId}.json`);
      if (!response.ok) throw new Error('No local logs available');

      const localVisits = await response.json();

      // Procesamiento similar al flujo principal
      const groupedVisits: VisitsByYear = {};

      for (const visit of localVisits) {
        const visitDate = new Date(visit.date);
        const year = visitDate.getFullYear().toString();

        if (!groupedVisits[year]) {
          groupedVisits[year] = [];
          // Expandir el a√±o actual por defecto
          setExpandedYears(prev => ({ ...prev, [year]: true }));
        }

        groupedVisits[year].push({
          ...visit,
          date: visitDate,
          events: (visit.events || []).map((event: any) => ({
            ...event,
            timestamp: new Date(event.timestamp)
          }))
        });
      }

      // Ordenar visitas dentro de cada a√±o (m√°s recientes primero)
      Object.keys(groupedVisits).forEach(year => {
        groupedVisits[year].sort((a, b) => b.date.getTime() - a.date.getTime());
      });

      setVisitsByYear(groupedVisits);
    } catch (err) {
      console.error('Error loading local visits:', err);

      // Si no hay datos locales, mostrar estado vac√≠o
      if (!patient) {
        setPatient({
          id: patientId || 'unknown',
          name: 'Paciente no encontrado',
          email: 'No disponible'
        });
      }

      setVisitsByYear({});
    }
  };

  // Manejar expansi√≥n de visitas
  const toggleVisit = (visitId: string) => {
    setExpandedVisits(prev => ({
      ...prev,
      [visitId]: !prev[visitId]
    }));
  };

  // Manejar expansi√≥n de a√±os
  const toggleYear = (year: string) => {
    setExpandedYears(prev => ({
      ...prev,
      [year]: !prev[year]
    }));
  };

  // Renderizar cabecera del paciente
  const renderPatientHeader = () => {
    if (!patient) return null;

    return (
      <div className="bg-white shadow sm:rounded-lg mb-6">
        <div className="px-4 py-5 sm:px-6">
          <div className="flex justify-between">
            <div>
              <h3 className="text-lg leading-6 font-medium text-gray-900">
                {patient.name}
              </h3>
              <p className="mt-1 max-w-2xl text-sm text-gray-500">
                {patient.email || 'Email no disponible'}
              </p>
            </div>
            <div className="text-right">
              <p className="text-sm text-gray-500">Fecha de nacimiento</p>
              <p className="text-sm font-medium text-gray-900">
                {patient.birthDate 
                  ? format(new Date(patient.birthDate), 'dd/MM/yyyy')
                  : 'No disponible'}
              </p>
            </div>
          </div>
        </div>
      </div>
    );
  };

  // Renderizar indicador de estado
  const renderStatusIndicator = (
    condition: boolean | undefined,
    isRequired: boolean = true,
    label: string
  ) => {
    if (condition === undefined) {
      return (
        <div className="flex items-center">
          <AlertCircle className="w-5 h-5 text-gray-400" />
          <span className="ml-2 text-gray-500">Dato no disponible</span>
        </div>
      );
    }

    if (condition) {
      return (
        <div className="flex items-center">
          <CheckCircle className="w-5 h-5 text-green-500" />
          <span className="ml-2 text-green-700">{label}</span>
        </div>
      );
    }

    return (
      <div className="flex items-center">
        <XCircle className="w-5 h-5 text-red-500" />
        <span className="ml-2 text-red-700">{isRequired ? `Falta ${label.toLowerCase()}` : `Sin ${label.toLowerCase()}`}</span>
      </div>
    );
  };

  // Renderizar lista de visitas por a√±o
  const renderVisitsByYear = () => {
    if (Object.keys(visitsByYear).length === 0) {
      return (
        <div className="bg-gray-50 border border-gray-200 text-gray-800 px-4 py-6 rounded text-center">
          No hay visitas registradas para este paciente
        </div>
      );
    }

    return (
      <div className="space-y-4">
        {Object.entries(visitsByYear)
          .sort(([yearA], [yearB]) => parseInt(yearB) - parseInt(yearA))
          .map(([year, visits]) => (
            <div key={year} className="border rounded-lg shadow-sm overflow-hidden">
              <div 
                className="bg-gray-50 p-4 flex justify-between items-center cursor-pointer hover:bg-gray-100"
                onClick={() => toggleYear(year)}
              >
                <div className="flex items-center space-x-3">
                  <Calendar className="w-5 h-5 text-gray-600" />
                  <h3 className="font-medium text-gray-800">A√±o {year}</h3>
                  <span className="bg-gray-200 text-gray-800 text-xs font-medium px-2.5 py-0.5 rounded-full">
                    {visits.length} visita{visits.length !== 1 ? 's' : ''}
                  </span>
                </div>
                {expandedYears[year] ? <ChevronUp className="w-5 h-5" /> : <ChevronDown className="w-5 h-5" />}
              </div>

              {expandedYears[year] && (
                <div className="divide-y divide-gray-200">
                  {visits.map((visit) => (
                    <div key={visit.id} className="bg-white">
                      <div 
                        className="p-4 hover:bg-gray-50 cursor-pointer"
                        onClick={() => toggleVisit(visit.id)}
                      >
                        <div className="flex justify-between items-start">
                          <div>
                            <p className="font-medium text-gray-900">
                              Visita {format(visit.date, 'dd MMM yyyy', { locale: es })}
                            </p>
                            <p className="text-sm text-gray-500">
                              ID: {visit.id}
                            </p>
                          </div>

                          <div className="flex items-center text-sm text-gray-500">
                            {expandedVisits[visit.id] ? (
                              <ChevronUp className="w-4 h-4" />
                            ) : (
                              <ChevronDown className="w-4 h-4" />
                            )}
                          </div>
                        </div>

                        {/* Badges de estado (visible siempre) */}
                        <div className="mt-3 grid grid-cols-1 md:grid-cols-3 gap-2">
                          {renderStatusIndicator(visit.isAudioApproved, true, 'Audio validado')}
                          {renderStatusIndicator(visit.isPdfSigned, true, 'PDF firmado')}
                          {renderStatusIndicator(visit.hasMcpContext, false, 'Contexto MCP')}
                        </div>
                      </div>

                      {/* Eventos detallados (expandibles) */}
                      {expandedVisits[visit.id] && (
                        <div className="px-4 pb-4 pt-1">
                          <div className="border-t border-gray-200 pt-3">
                            <h4 className="text-sm font-medium text-gray-900 mb-2">
                              Detalles de eventos
                            </h4>

                            {visit.events.length === 0 ? (
                              <p className="text-sm text-gray-500">No hay eventos detallados disponibles</p>
                            ) : (
                              <ul className="space-y-2">
                                {visit.events
                                  .sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime())
                                  .map(event => {
                                    const eventIcons: Record<EventType, React.ReactNode> = {
                                      'form.update': <FileText className="w-4 h-4 text-blue-500" />,
                                      'audio.review': <Mic className="w-4 h-4 text-green-500" />,
                                      'pdf.export': <FilePdf className="w-4 h-4 text-red-500" />,
                                      'mcp.context.build': <Database className="w-4 h-4 text-purple-500" />
                                    };

                                    const eventLabels: Record<EventType, string> = {
                                      'form.update': 'Actualizaci√≥n de formulario',
                                      'audio.review': 'Revisi√≥n de audio',
                                      'pdf.export': 'Exportaci√≥n de PDF',
                                      'mcp.context.build': 'Creaci√≥n de contexto MCP'
                                    };

                                    return (
                                      <li key={event.id} className="text-sm">
                                        <div className="flex items-center">
                                          {eventIcons[event.type]}
                                          <span className="ml-2 font-medium">{eventLabels[event.type]}</span>
                                          <span className="ml-2 text-gray-500">
                                            {format(event.timestamp, 'dd MMM yyyy, HH:mm', { locale: es })}
                                          </span>
                                        </div>

                                        {event.type === 'audio.review' && (
                                          <div className="ml-6 mt-1">
                                            {event.details?.approved 
                                              ? <span className="text-green-600">‚úì Audio aprobado</span>
                                              : <span className="text-red-600">‚úó Audio rechazado</span>
                                            }
                                          </div>
                                        )}

                                        {event.type === 'pdf.export' && (
                                          <div className="ml-6 mt-1">
                                            {event.details?.signed 
                                              ? <span className="text-green-600">‚úì Documento firmado digitalmente</span>
                                              : <span className="text-yellow-600">‚ö† Documento sin firma digital</span>
                                            }
                                          </div>
                                        )}
                                      </li>
                                    );
                                  })
                                }
                              </ul>
                            )}
                          </div>
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              )}
            </div>
          ))}
      </div>
    );
  };

  return (
    <div className="bg-gray-100 rounded-lg p-6">
      <h2 className="text-2xl font-bold mb-6">Historial de Visitas del Paciente</h2>

      {/* Datos del paciente */}
      {renderPatientHeader()}

      {/* Cargando o error */}
      {loading ? (
        <div className="flex justify-center items-center h-40" role="status">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
        </div>
      ) : error ? (
        <div className="bg-red-50 border border-red-200 text-red-800 px-4 py-3 rounded">
          {error}
        </div>
      ) : (
        renderVisitsByYear()
      )}
    </div>
  );
};

export default PatientAuditDashboard;

import { useState  } from 'react';
import { Box, Button, Snackbar, Alert, CircularProgress, Tooltip, Typography } from '@mui/material';
import PictureAsPdfIcon from '@mui/icons-material/PictureAsPdf';
import VerifiedIcon from '@mui/icons-material/Verified';
import InfoOutlinedIcon from '@mui/icons-material/InfoOutlined';
import ExportService from '@/core/services/export/ExportService';
import type { Visit, Patient, User, PatientEval  } from '@/core/types';
import { useSession } from '@/core/hooks/useSession';

// Tipo m√°s flexible para permitir compatibilidad entre diferentes versiones de Visit
type VisitLike = {
  id: string;
  patientId: string;
  date: string;
  status: string;
  [key: string]: any;
};

// Tipo m√°s flexible para evaluaciones
type EvaluationLike = {
  patientId: string;
  [key: string]: any;
};

interface VisitPDFExporterProps {
  visit: VisitLike;
  patient: Patient;
  evaluation?: EvaluationLike;
  onExportStart?: () => void;
  onExportComplete?: () => void;
  onExportError?: (error: Error) => void;
  buttonText?: string;
  className?: string;
}

/**
 * Componente para exportar una visita a PDF
 * 
 * @param props - Propiedades del componente
 * @returns Componente React
 */
const VisitPDFExporter: React.FC<VisitPDFExporterProps> = ({
  visit,
  patient,
  evaluation,
  onExportStart,
  onExportComplete,
  onExportError,
  buttonText = 'Exportar PDF',
  className
}) => {
  const { session } = useSession();
  const [loading, setLoading] = useState(false);
  const [verifyLoading, setVerifyLoading] = useState(false);
  const [lastSignatureInfo, setLastSignatureInfo] = useState<{
    hash?: string;
    timestamp?: string;
    success?: boolean;
  } | null>(null);
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: '',
    severity: 'success' as 'success' | 'error' | 'info' | 'warning'
  });

  // Verificar si el usuario actual tiene permisos para exportar
  const canExport = session?.user?.role === 'professional' || session?.user?.role === 'admin';

  /**
   * Gestiona la exportaci√≥n a PDF
   */
  const handleExport = async () => {
    if (!canExport || !session?.user) {
      setSnackbar({
        open: true,
        message: 'No tienes permisos para exportar esta visita',
        severity: 'error'
      });
      return;
    }

    try {
      setLoading(true);

      // Notificar inicio de exportaci√≥n
      onExportStart?.();

      // Crear el profesional a partir de la sesi√≥n
      const professional: User = {
        id: session.user.id,
        name: session.user.name,
        email: session.user.email,
        role: session.user.role
      };

      // Normalizar la visita para cumplir con la interfaz Visit
      const normalizedVisit: Visit = {
        id: visit.id,
        patientId: visit.patientId,
        date: visit.date,
        status: visit.status as 'scheduled' | 'completed' | 'cancelled',
        professionalId: visit.professionalId || professional.id,
        updatedAt: visit.updatedAt || new Date().toISOString(),
        createdAt: visit.createdAt || new Date().toISOString(),
      };

      // Normalizar la evaluaci√≥n (si existe)
      const normalizedEvaluation = evaluation ? {
        visitId: visit.id,
        ...evaluation
      } as PatientEval : undefined;

      // Generar el PDF con firma digital
      console.log(`Generando PDF para visita ${visit.id} (${patient.firstName} ${patient.lastName})`);
      const pdfBytes = await ExportService.generateVisitPDF(
        normalizedVisit, 
        patient, 
        professional, 
        normalizedEvaluation,
        true // Habilitar firma digital
      );

      // Crear un blob con los bytes del PDF
      const blob = new Blob([pdfBytes], { type: 'application/pdf' });

      // Crear URL para el blob
      const url = URL.createObjectURL(blob);

      // Crear elemento <a> para la descarga
      const link = document.createElement('a');
      link.href = url;
      link.download = `Visita_${visit.id}.pdf`;

      // Simular clic en el enlace para iniciar la descarga
      document.body.appendChild(link);
      link.click();

      // Limpiar
      document.body.removeChild(link);
      URL.revokeObjectURL(url);

      // Verificar la firma reci√©n creada
      const verificationResult = await ExportService.verifyDocumentSignature(
        normalizedVisit.id,
        pdfBytes
      );

      if (verificationResult.valid && verificationResult.signature) {
        // Guardar informaci√≥n de la firma
        setLastSignatureInfo({
          hash: verificationResult.signature.hash,
          timestamp: verificationResult.signature.created_at,
          success: true
        });

        // Mostrar mensaje de √©xito con informaci√≥n de firma
        setSnackbar({
          open: true,
          message: 'PDF generado, firmado digitalmente y descargado correctamente',
          severity: 'success'
        });
      } else {
        // Guardar informaci√≥n de error de firma
        setLastSignatureInfo({
          success: false
        });

        // Mostrar mensaje de √©xito pero con advertencia sobre firma
        setSnackbar({
          open: true,
          message: 'PDF generado y descargado, pero no se pudo verificar la firma digital',
          severity: 'warning'
        });
      }

      console.log(`PDF generado y descargado: Visita_${visit.id}.pdf`);

      // Notificar finalizaci√≥n
      onExportComplete?.();
    } catch (error) {
      console.error('Error al exportar la visita a PDF:', error);

      // Mostrar mensaje de error
      setSnackbar({
        open: true,
        message: 'Error al generar el PDF. Int√©ntalo de nuevo.',
        severity: 'error'
      });

      // Notificar error
      if (error instanceof Error) {
        onExportError?.(error);
      } else {
        onExportError?.(new Error('Error desconocido al exportar PDF'));
      }
    } finally {
      setLoading(false);
    }
  };

  /**
   * Verificar una firma digital desde un archivo PDF subido por el usuario
   */
  const handleVerifySignature = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = event.target.files;
    if (!files || files.length === 0) return;

    try {
      setVerifyLoading(true);

      // Leer el archivo PDF
      const file = files[0];
      const fileData = await file.arrayBuffer();
      const fileBytes = new Uint8Array(fileData);

      // Verificar la firma
      const verificationResult = await ExportService.verifyDocumentSignature(
        visit.id,
        fileBytes
      );

      if (verificationResult.valid && verificationResult.signature) {
        // Guardar informaci√≥n de la firma verificada
        setLastSignatureInfo({
          hash: verificationResult.signature.hash,
          timestamp: verificationResult.signature.created_at,
          success: true
        });

        // Mostrar mensaje de √©xito
        setSnackbar({
          open: true,
          message: 'El documento es aut√©ntico y su firma digital es v√°lida',
          severity: 'success'
        });
      } else {
        // Guardar informaci√≥n de error
        setLastSignatureInfo({
          success: false
        });

        // Mostrar mensaje de error
        setSnackbar({
          open: true,
          message: verificationResult.error || 'El documento no tiene una firma digital v√°lida',
          severity: 'error'
        });
      }
    } catch (error) {
      console.error('Error al verificar firma digital:', error);

      // Mostrar mensaje de error
      setSnackbar({
        open: true,
        message: 'Error al verificar la firma digital. Int√©ntalo de nuevo.',
        severity: 'error'
      });
    } finally {
      setVerifyLoading(false);
      // Limpiar el input de archivo
      event.target.value = '';
    }
  };

  // Si el usuario no tiene permisos, no mostrar el bot√≥n
  if (!canExport) return null;

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
        <Button
          variant="contained"
          color="primary"
          startIcon={loading ? <CircularProgress size={20} color="inherit" /> : <PictureAsPdfIcon />}
          onClick={handleExport}
          disabled={loading || verifyLoading}
          className={className}
          sx={{
            backgroundColor: '#1976d2',
            '&:hover': {
              backgroundColor: '#1565c0',
            }
          }}
        >
          {loading ? 'Generando...' : buttonText}
        </Button>

        {/* Bot√≥n para verificar firma */}
        <Tooltip title="Verificar autenticidad de un documento PDF">
          <Button
            variant="outlined"
            color="secondary"
            component="label"
            startIcon={verifyLoading ? <CircularProgress size={20} color="inherit" /> : <VerifiedIcon />}
            disabled={loading || verifyLoading}
            sx={{ ml: 1 }}
          >
            {verifyLoading ? 'Verificando...' : 'Verificar PDF'}
            <input
              type="file"
              accept=".pdf"
              hidden
              onChange={handleVerifySignature}
              onClick={(e) => {
                // Permitir seleccionar el mismo archivo varias veces
                (e.target as HTMLInputElement).value = '';
              }}
            />
          </Button>
        </Tooltip>

        {lastSignatureInfo && (
          <Tooltip
            title={
              lastSignatureInfo.success
                ? `Documento firmado digitalmente el ${new Date(lastSignatureInfo.timestamp || '').toLocaleString()}. 
                   Hash: ${lastSignatureInfo.hash}`
                : 'No se ha podido verificar la firma digital del documento'
            }
          >
            <Box sx={{ display: 'flex', alignItems: 'center', ml: 1 }}>
              <InfoOutlinedIcon
                color={lastSignatureInfo.success ? 'success' : 'warning'}
                fontSize="small"
              />
              <Typography variant="caption" sx={{ ml: 0.5 }}>
                {lastSignatureInfo.success ? 'Firmado digitalmente' : 'Sin firma verificada'}
              </Typography>
            </Box>
          </Tooltip>
        )}
      </Box>

      <Snackbar 
        open={snackbar.open} 
        autoHideDuration={6000} 
        onClose={() => setSnackbar({ ...snackbar, open: false })}
      >
        <Alert 
          onClose={() => setSnackbar({ ...snackbar, open: false })} 
          severity={snackbar.severity}
          sx={{ width: '100%' }}
        >
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default VisitPDFExporter;

