// Clase personalizada para errores de evaluaci√≥n
export class EvalServiceError extends Error {
  constructor(
    message: string,
    public readonly code: string,
    public readonly originalError?: unknown
  ) {
    super(message);
    this.name = 'EvalServiceError';
  }
}

// Funci√≥n auxiliar para retry con backoff exponencial
const retry = async <T>(
  fn: () => Promise<T>,
  maxAttempts: number = 3,
  baseDelay: number = 1000
): Promise<T> => {
  let lastError: unknown;

  for (let attempt = 1; attempt <= maxAttempts; attempt++) {
    try {
      return await fn();
    } catch (error) {
      lastError = error;
      if (attempt === maxAttempts) break;

      const delay = baseDelay * Math.pow(2, attempt - 1);
      await new Promise(resolve => setTimeout(resolve, delay));
    }
  }

  throw lastError;
};

// Funci√≥n auxiliar para validar y parsear respuesta JSON
const parseJSONResponse = async (response: Response): Promise<unknown> => {
  const contentType = response.headers.get('content-type');

  if (!contentType?.includes('application/json')) {
    const text = await response.text();
    throw new EvalServiceError(
      'Respuesta no v√°lida: Se esperaba JSON',
      'INVALID_CONTENT_TYPE',
      { contentType, responseText: text }
    );
  }

  try {
    return await response.json();
  } catch (error) {
    throw new EvalServiceError(
      'Error al parsear respuesta JSON',
      'PARSE_ERROR',
      error
    );
  }
};

export interface EvalFilter {
