interface LangfuseConfig {
  publicKey?: string;
  baseUrl?: string;
}

interface TraceOptions {
  name: string;
  metadata?: Record<string, unknown>;
}

// Par√°metros posibles para eventos
export interface EventParams {
  name: string;
  payload?: Record<string, unknown>;
  traceId?: string;
}

export type EventOptions = string | EventParams;

// Configuraci√≥n para Langfuse Server
const langfuseConfig: LangfuseConfig = {
  publicKey: process.env.VITE_LANGFUSE_PUBLIC_KEY,
  baseUrl: process.env.VITE_LANGFUSE_BASE_URL
};

// Funci√≥n para verificar si tenemos las credenciales necesarias
