/**
 * Adaptador para simular m√©todos de Zod que no existen
 * o tienen una API diferente en nuestra implementaci√≥n.
 */
import type { z, Schema  } from '@/types/zod-utils';

// Tipos para compatibilidad
type ZodType<T> = Schema<T>;
type ZodTypeAny = ZodType<any>;

// Funci√≥n para crear un enum
export function createEnum<T extends [string, ...string[]]>(values: T) {
  return z.enumValues(values);
}

// Funci√≥n para crear un literal
export function createLiteral<T extends string | number | boolean>(value: T) {
  return z.literal(value);
}

// Funci√≥n para crear una uni√≥n discriminada
export function createDiscriminatedUnion<
  K extends string,
  Options extends ZodType<any>[]
>(discriminator: K, options: Options) {
  // Utilizamos la versi√≥n con spread operator para compatibilidad
  return z.union(...(options as any));
}

// Funci√≥n para crear una uni√≥n
export function createUnion<T extends ZodType<any>[]>(
  types: T
) {
  // Utilizamos la versi√≥n con spread operator para compatibilidad
  return z.union(...(types as any));
}

// Tipo de inferencia
export type Infer<T extends ZodTypeAny> = T['_type'];

export const zodAdapter = {
  createEnum,
  createLiteral,
  createDiscriminatedUnion,
  createUnion,
};

/**
 * Este archivo proporciona un "shim" o adaptador que permite
 * usar parte de la API de zod pero mapeando a nuestra propia
 * implementaci√≥n de validaci√≥n.
 */
import * as z from '@/types/zod-utils';

// Re-exportamos el objeto z para un uso m√°s sencillo
export const zod = z;

// Tipo para representar cualquier esquema de validaci√≥n
export type ZodType<T> = z.Schema<T>;
export type ZodRawShape = Record<string, ZodType<any>>;
export type ZodTypeAny = ZodType<any>;

// Funci√≥n para crear un esquema de enumeraci√≥n
export function createEnum<T extends readonly [string, ...string[]]>(values: T) {
  return z.enumValues(values);
}

// Funci√≥n para crear un esquema de objeto
export function createObject<T extends Record<string, any>>(shape: Record<string, ZodType<any>>) {
  return z.object(shape);
}

// Funci√≥n para crear un esquema de array
export function createArray<T>(schema: ZodType<T>) {
  return z.array(schema);
}

// Funciones b√°sicas
export function createString() {
  return z.string();
}

export function createNumber() {
  return z.number();
}

export function createBoolean() {
  return z.boolean();
}

export function createOptional<T>(schema: ZodType<T>) {
  return schema.optional();
}

// Tipo para inferencia
export type Infer<T extends ZodType<any>> = T['_type'];

// Funci√≥n para validar datos
export function parse<T>(schema: ZodType<T>, data: unknown): T {
  return schema.parse(data);
}

// Exportaciones para compatibilidad
export default {
  enum: createEnum,
